mapper.select:

SELECT
	status
FROM attendance
WHERE TO_CHAR(att_date, 'YYYYMMDD') = #{attDate}
AND u_id = #{userId}

mapper.update:

UPDATE attendance
SET att_time = sysdate,
	status = #{choice}
WHERE u_id = #{userId}
AND TO_CHAR(att_date, 'YY/MM/DD') = #{attDate}

mapper.select:

SELECT TO_CHAR(att_time, 'HH:MI:SS') AS att_time
FROM ATTENDANCE
WHERE to_char(att_date, 'YYYYMMDD') = #{today}
AND u_id = #{userId}


SELECT TO_CHAR(leave_time, 'HH24:MI:SS') AS leave_time
FROM ATTENDANCE
WHERE to_char(att_date, 'YYYYMMDD') = #{today}
AND u_id = #{userId}

mapper.update:

UPDATE attendance
SET leave_time = sysdate
WHERE u_id = ${userId}
AND TO_CHAR(att_date, 'YY/MM/DD') = #{attDate}


UPDATE ATTENDANCE
SET STATUS = '결근'
WHERE att_no = #{attNo}

mapper.select:

